import com.microsoft.playwright.*;import org.testng.annotations.AfterMethod;import org.testng.annotations.BeforeMethod;import pages.SearchPage;public class BaseTest {   protected SearchPage searchPage;        Page page;    Playwright playwright;        @BeforeMethod    public void setUp() {        playwright = Playwright.create();        Browser browser = playwright.chromium().launch(new BrowserType.LaunchOptions().setHeadless(false));        BrowserContext browserContext =browser.newContext( new Browser.NewContextOptions()                .setViewportSize(1000, 660)                .setBaseURL("https://askomdch.com/"));        page = browserContext.newPage();                searchPage = new SearchPage(page);    }        @AfterMethod    public void close () {        page.context().browser().close();        playwright.close();    }}