import com.microsoft.playwright.Browser;import com.microsoft.playwright.BrowserType;import com.microsoft.playwright.Page;import com.microsoft.playwright.Playwright;import org.testng.annotations.AfterMethod;import org.testng.annotations.BeforeMethod;import org.testng.annotations.Test;import pages.HomePage;import pages.LoginPage;public class LoginTest{            Playwright playwright;        Browser browser;        Page page;                @BeforeMethod        public void setup() {            playwright = Playwright.create();            browser = playwright.chromium().launch(new BrowserType.LaunchOptions().setHeadless(false));            page = browser.newPage();        }                @Test        public void testLogin() {            LoginPage loginPage = new LoginPage(page);            loginPage.navigateTo("https://magento.softwaretestingboard.com/customer/account/login/");            HomePage homePage = loginPage.login("test@example.com", "password123");                        String welcomeMessage = homePage.getWelcomeMessage();            assert welcomeMessage.contains("Welcome");        }                @AfterMethod        public void tearDown() {            browser.close();            playwright.close();        }}